    private static final Color color = new Color(0, 105, 210, 125);

    public SupportSprite(final Gob g, final float angle, int size) {
        super(g, null);
        if (size == 0) {
            this.mesh = ColoredSupportMesh.getMesh(color, positionSmallest, angle, 0.2f);
        } else if (size == 1) {
            this.mesh = ColoredSupportMesh.getMesh(color, positionsSmall, angle, 0.3f);
        } else if (size == 2) {
            this.mesh = ColoredSupportMesh.getMesh(color, positionsMedium, angle, 0.4f);
        } else if (size == 3){
            this.mesh = ColoredSupportMesh.getMesh(color, positionsLarge, angle, 0.5f);
        } else {
            this.mesh = ColoredSupportMesh.getMesh(color, positionSmallest, angle, 0.2f);
        }
    }

    public void rem() {
        alive = false;
    }

    @Override
    public boolean tick(double ddt) {
        return !alive;
    }

    @Override
    public void added(RenderTree.Slot slot) {
        super.added(slot);
        slot.add(mesh, Pipe.Op.compose(new BaseColor(color), Clickable.No));
    }
